import {
  Aware,
  RoleAcl,
  Statement,
  UserAcl
  } from "../interfaces/acl"
import { Storage } from "../interfaces/driver"
import { RoleAware } from "./role-aware"
import { UserAware } from "./user-aware"

export class Acl {

  public constructor(public storage: Storage) {
  }

  public user(name: string): Aware {
    return new UserAware(this.storage, name)
  }

  public role(name: string): Aware {
    return new RoleAware(this.storage, name)
  }
}
