
export interface UserAcl extends HasStatements {
  name: string
  groups?: string[]
  policies?: string[]
}

export interface GroupAcl extends HasStatements {
  name: string
  policies?: string[]
}

export interface RoleAcl extends HasStatements {
  name: string
  policies?: string[]
}

export interface PolicyAcl extends HasStatements {
  name: string
}

export interface HasStatements {
  allows?: Statement[]
  denies?: Statement[]
}

export interface Statement {
  actions: string|string[]
  resources: string|string[]
}

export interface Aware {
  can(action: string, resources: string|string[]): Promise<boolean>
  cannot(action: string, resources: string|string[]): Promise<boolean>
}
